import React, { useState } from 'react';
import { ListBase, useTranslate } from 'react-admin';
import { Container, Grid, Hidden, useMediaQuery, AppBar, Tabs, Tab, makeStyles, Box } from '@material-ui/core';
import { useCheckAuthenticated } from '@semapps/auth-provider';
import { List } from 'react-admin';
import { MapList } from '@semapps/geo-components';
import CardsList from '../../commons/lists/CardsList';
import EventCard from './EventCard';
import HeaderTitle from '../../layout/HeaderTitle';
import ProfileCard from '../../commons/cards/ProfileCard';
import Alert from "@material-ui/lab/Alert";
import AppIcon from '../../config/AppIcon';

const useStyles = makeStyles((theme) => ({
  appBar: {
    backgroundColor: theme.palette.grey['300'],
    boxShadow: 'none',
    zIndex: 900,
  },
  mission: {
    backgroundColor: theme.palette.primary.main,
    [theme.breakpoints.up('xs')]: {
      marginTop: 5,
    }
  }
}));

const EventList = (props) => {
  useCheckAuthenticated();
  const record = useRecordContext(rest);
  const classes = useStyles();
  const [tab, setTab] = useState(0);
  const xs = useMediaQuery((theme) => theme.breakpoints.down('xs'), { noSsr: true });
  const translate = useTranslate();
  return (
    <>

            <ListBase
              perPage={1000}
              {...props}
            >
              <MapList
                address={(record) => (
                  <>
                    {record?.['vcard:given-name'] + ', ' + record?.['vcard:hasAddress']?.['vcard:given-name']}
                    {record?.['vcard:note'] && (
                      <Box mb={2} mt={2}>
                        <Alert severity="info">
                          <strong>{translate('resources.Location.fields.vcard:note')}</strong>: {record?.['vcard:note']}
                        </Alert>
                      </Box>
                    )}
                  </>
                )}
                latitude={(record) => record?.['vcard:hasAddress']?.['vcard:hasGeo']?.['vcard:latitude']}
                longitude={(record) => record?.['vcard:hasAddress']?.['vcard:hasGeo']?.['vcard:longitude']}
                label={record => record.label}
                description={record => record.description}
              />
            </ListBase>
    </>
  );
};

export default EventList;
