import React from 'react';
import { TextField, useTranslate } from 'react-admin';

import { Button, makeStyles } from '@material-ui/core';
import ButtonGroup from '@material-ui/core/ButtonGroup';
import ShareIcon from '@material-ui/icons/Share';
import Container from '@material-ui/core/Container';
import FaceIcon from '@material-ui/icons/Face';

import { ReferenceField } from '@semapps/field-components';
import IconsList from '../../commons/lists/IconsList';

import AvatarField from '../../commons/fields/AvatarField.js';



const useStyles = makeStyles((theme) => ({
  root: {
    marginTop: 5,
    marginBottom: 24,
  },
  title: {
    backgroundRepeat: 'no-repeat',
    backgroundSize: 'cover',
    backgroundImage: `radial-gradient(circle at 50% 10em, ${theme.palette.primary.light} 0%, ${theme.palette.primary.main} 100%)`,
    color: theme.palette.primary.contrastText,
    height: 90,
    position: 'relative',
  },
  avatarWrapper: {
    position: 'absolute',
    margin: 15,
    top: 0,
    left: 0,
    right: 0,
    textAlign: 'center',
  },
  avatar: {
    width: 140,
    height: 140,
  },
  block: {
    backgroundColor: 'white',
    paddingTop: 70,
    paddingBottom: 20,
  },
}));






const EventDetails = (props) => {
  const translate = useTranslate();
  return (
    <>
      <Container>
        <Card className={classes.root}>
          <Box className={classes.title}>
            <Box display="flex" justifyContent="center" className={classes.avatarWrapper}>
              <Avatar
                src={computedImage || computedFallback}
                alt={computedLabel}
                fallback={computedFallback}
                className={classes.avatar}
                variant={variant}
              />
            </Box>
          </Box>
          <Box className={classes.block}>
            <Typography variant="h4" align="center">
              {computedLabel}
            </Typography>
            <Typography variant="subtitle1" align="center">
              {formatUsername(computedUsername)}
            </Typography>
          </Box>
        </Card>
      </Container>



      <Container>
        <IconsList {...props}>
          <ReferenceField reference="Actor" source="dc:creator" icon={<FaceIcon />} link={true}>
            <ReferenceField reference="Profile" source="url">

              <TextField source="vcard:given-name" />
            </ReferenceField>
          </ReferenceField>      
        </IconsList>
      </Container>
      <Container>
        <ButtonGroup variant="contained" aria-label="outlined primary button group">
          <Button color="secondary">
            {translate('app.action.contact')}
          </Button>

          <Button color="secondary" label={translate('app.action.share')} >
            <ShareIcon />
          </Button>
        </ButtonGroup>
      </Container>
    </>
  );
};

export default EventDetails;
