import jsonld from 'jsonld';

const fetchResource = async (resourceUri, config) => {
  const { httpClient, jsonContext } = config;

  let { json: data } = await httpClient(resourceUri);

  data.id = data.id || data['@id'];

  // We compact only if the context is different between the frontend and the middleware
  // TODO deep compare if the context is an object
  if (data['@context'] !== jsonContext) {
    data = await jsonld.compact(data, jsonContext);
  }

  return data;
};

export default fetchResource;
import fetchResource from '../utils/fetchResource';

const getOneMethod = config => async (resourceId, params) => {
  const { resources } = config;
  const dataModel = resources[resourceId];

  if (!dataModel) throw new Error(`Resource ${resourceId} is not mapped in resources file`);

  const data = await fetchResource(params.id, config);

  // Transform single value into array if forceArray is set
  if (dataModel.list?.forceArray) {
    for (const forceArrayItem of dataModel.list?.forceArray) {
      if (data[forceArrayItem] && !Array.isArray(data[forceArrayItem])) {
        data[forceArrayItem] = [data[forceArrayItem]];
      }
    }
  }

  // TODO activate defaultFetchPlan option
  // if (dataModel.list?.defaultFetchPlan) {
  //   for (const node of dataModel.list?.defaultFetchPlan) {
  //     if (
  //       data[node] &&
  //       typeof data[node] === 'string' &&
  //       data[node].startsWith('http')
  //     ) {
  //       try {
  //         const dataToEmbed = await fetchResource(data[node], config);
  //         delete dataToEmbed['@context'];
  //         data[node] = dataToEmbed;
  //       } catch (e) {
  //         // Ignore errors (this may happen if user does not have rights to see the resource)
  //       }
  //     }
  //   }
  // }

  return { data };
};

export default getOneMethod;
import createSlug from 'speakingurl';
import urlJoin from 'url-join';

export const getSlugWithExtension = fileName => {
  let fileExtension = '';
  let splitFileName = fileName.split('.');
  if (splitFileName.length > 1) {
    fileExtension = splitFileName.pop();
    fileName = splitFileName.join('.');
  }
  return createSlug(fileName, { lang: 'fr' }) + '.' + fileExtension;
};

export const isFile = o => o && o.rawFile && o.rawFile instanceof File;

const getUploadsContainerUri = config => {
  const serverKey = Object.keys(config.dataServers).find(key => config.dataServers[key].uploadsContainer);
  if (serverKey) {
    return urlJoin(config.dataServers[serverKey].baseUrl, config.dataServers[serverKey].uploadsContainer);
  }
};

const uploadFile = async (rawFile, config) => {
  const uploadsContainerUri = getUploadsContainerUri(config);
  if (!uploadsContainerUri) throw new Error("You must define an uploadsContainer in one of the server's configuration");

  console.log('upload raw file', typeof rawFile, rawFile)

  const response = await config.httpClient(uploadsContainerUri, {
    method: 'POST',
    body: rawFile,
    headers: new Headers({
      // We must sluggify the file name, because we can't use non-ASCII characters in the header
      // However we keep the extension apart (if it exists) so that it is not replaced with a -
      // TODO let the middleware guess the extension based on the content type
      Slug: getSlugWithExtension(rawFile.name),
      'Content-Type': rawFile.type
    })
  });

  if (response.status === 201) {
    return response.headers.get('Location');
  }
};

/*
 * Look for raw files in the record data.
 * If there are any, upload them and replace the file by its URL.
 */
const uploadAllFiles = async (record, config) => {
  for (let property in record) {
    if (record.hasOwnProperty(property)) {
      if (Array.isArray(record[property])) {
        for (let i = 0; i < record[property].length; i++) {
          if (isFile(record[property][i])) {
            record[property][i] = await uploadFile(record[property][i].rawFile, config);
          }
        }
      } else {
        if (isFile(record[property])) {
          record[property] = await uploadFile(record[property].rawFile, config);
        }
      }
    }
  }
  return record;
};

export default uploadAllFiles;
const getServerKeyFromType = (type, dataServers) => {
  return Object.keys(dataServers).find(key => {
    return dataServers[key][type];
  });
};

export default getServerKeyFromType;
import getServerKeyFromType from './getServerKeyFromType';

const parseServerKey = (serverKey, dataServers) => {
  switch (serverKey) {
    case '@default':
      return getServerKeyFromType('default', dataServers);
    case '@pod':
      return getServerKeyFromType('pod', dataServers);
    case '@authServer':
      return getServerKeyFromType('authServer', dataServers);
    default:
      return serverKey;
  }
};

// Return the list of servers keys in an array
// parsing keywords like @all, @default, @pod and @authServer
const parseServerKeys = (serverKeys, dataServers) => {
  if (Array.isArray(serverKeys)) {
    if (serverKeys.includes('@all')) {
      return Object.keys(dataServers);
    } else {
      return serverKeys.map(serverKey => parseServerKey(serverKey, dataServers));
    }
  } else if (typeof serverKeys === 'string') {
    if (serverKeys === '@all') {
      return Object.keys(dataServers);
    } else if (serverKeys === '@remote') {
      const defaultServerKey = getServerKeyFromType('default', dataServers);
      return Object.keys(dataServers).filter(serverKey => serverKey !== defaultServerKey);
    } else {
      return [parseServerKey(serverKeys, dataServers)];
    }
  } else {
    // If server key is empty
    return false;
  }
};

export default parseServerKeys;
import urlJoin from 'url-join';
import parseServerKeys from './parseServerKeys';

const findContainersWithTypes = (types, serverKeys, dataServers) => {
  let containers = {};
  let existingContainers = [];

  serverKeys = parseServerKeys(serverKeys, dataServers);

  Object.keys(dataServers).forEach(key1 => {
    Object.keys(dataServers[key1].containers).forEach(key2 => {
      if (!serverKeys || serverKeys.includes(key2)) {
        Object.keys(dataServers[key1].containers[key2]).forEach(type => {
          if (types.includes(type)) {
            dataServers[key1].containers[key2][type].map(path => {
              const containerUri = urlJoin(dataServers[key2].baseUrl, path);

              // Avoid returning the same container several times
              if (!existingContainers.includes(containerUri)) {
                existingContainers.push(containerUri);

                if (!containers[key1]) containers[key1] = [];
                containers[key1].push(containerUri);
              }
            });
          }
        });
      }
    });
  });
  return containers;
};

export default findContainersWithTypes;
import urlJoin from 'url-join';
import getOne from './getOne';
import uploadAllFiles from '../utils/uploadAllFiles';
import findContainersWithTypes from '../utils/findContainersWithTypes';

const createMethod = config => async (resourceId, params) => {
  const { dataServers, resources, httpClient, jsonContext } = config;
  const dataModel = resources[resourceId];

  if (!dataModel) Error(`Resource ${resourceId} is not mapped in resources file`);

  const headers = new Headers();

  let containerUri, serverKey;
  if (dataModel.create?.container) {
    serverKey = Object.keys(dataModel.create.container)[0];
    containerUri = urlJoin(dataServers[serverKey].baseUrl, Object.values(dataModel.create.container)[0]);
  } else {
    serverKey = dataModel.create?.server || Object.keys(dataServers).find(key => dataServers[key].default === true);
    if (!serverKey) throw new Error('You must define a server for the creation, or a container, or a default server');

    const containers = findContainersWithTypes(dataModel.types, [serverKey], dataServers);
    // Extract the containerUri from the results (and ensure there is only one)
    const serverKeys = Object.keys(containers);

    if (!serverKeys || serverKeys.length === 0)
      throw new Error(`No container with types ${JSON.stringify(dataModel.types)} found on server ${serverKey}`);
    if (serverKeys.length > 1 || containers[serverKeys[0]].length > 1)
      throw new Error(
        `More than one container detected with types ${JSON.stringify(dataModel.types)} on server ${serverKey}`
      );
    containerUri = containers[serverKeys[0]][0];
  }

  if (params.data) {
    if (dataModel.fieldsMapping?.title) {
      if (Array.isArray(dataModel.fieldsMapping.title)) {
        headers.set('Slug', dataModel.fieldsMapping.title.map(f => params.data[f]).join(' '));
      } else {
        headers.set('Slug', params.data[dataModel.fieldsMapping.title]);
      }
    }

    // Upload files, if there are any
    params.data = await uploadAllFiles(params.data, config);

    const { headers: responseHeaders } = await httpClient(containerUri, {
      method: 'POST',
      headers,
      body: JSON.stringify({
        '@context': jsonContext,
        '@type': dataModel.types,
        ...params.data
      })
    });

    // Retrieve newly-created resource
