const buildUnionQuery = queries =>
  queries.map(q => {
    let triples = q.query;
    const firstTriple = queries.find(q2 => q.parentNode === q2.node);
    if (firstTriple !== undefined) {
      triples = triples.concat(firstTriple.query[0]);
    }
    return {
      type: 'bgp',
      triples
    };
  });

