import React from 'react';
import { DateField, TextField, useTranslate, ListGuesser, Resource } from 'react-admin';
import { Button } from '@material-ui/core';
import ButtonGroup from '@material-ui/core/ButtonGroup';
import ShareIcon from '@material-ui/icons/Share';
import Container from '@material-ui/core/Container';
import { ReferenceField } from '@semapps/field-components';
import IconsList from '../../commons/lists/IconsList';
import FaceIcon from '@material-ui/icons/Face';

const EventDetails = (props) => {
  const translate = useTranslate();
  return (
    <>
      <Container>
        <IconsList {...props}>
          <ReferenceField reference="Actor" source="dc:creator" icon={<FaceIcon />} link={true}>
            <ReferenceField reference="Profile" source="url">

              <TextField source="vcard:given-name" />
            </ReferenceField>
          </ReferenceField>      
        </IconsList>
      </Container>
      <Container>
        <ButtonGroup variant="contained" aria-label="outlined primary button group">
          <Button color="secondary">
            {translate('app.action.contact')}
          </Button>

          <Button color="secondary" label={translate('app.action.share')} >
            <ShareIcon />
          </Button>
        </ButtonGroup>
      </Container>
    </>
  );
};

export default EventDetails;
