import urlJoin from 'url-join';

export const defaultToArray = value => (!value ? undefined : Array.isArray(value) ? value : [value]);

// Transform the URI to the one used to find the ACL
// To be compatible with all servers, we should do a HEAD request to the resource URI
export const getAclUri = resourceUri => {
  const parsedUrl = new URL(resourceUri);
  return urlJoin(parsedUrl.origin, '_acl', parsedUrl.pathname);
};

export const getAclContext = baseUri => ({
  '@base': baseUri,
  acl: 'http://www.w3.org/ns/auth/acl#',
  foaf: 'http://xmlns.com/foaf/0.1/',
  'acl:agent': { '@type': '@id' },
  'acl:agentGroup': { '@type': '@id' },
  'acl:agentClass': { '@type': '@id' },
  'acl:mode': { '@type': '@id' },
  'acl:accessTo': { '@type': '@id' }
});

export const getAuthServerUrl = async dataProvider => {
  const dataServers = await dataProvider.getDataServers();
  const authServer = Object.values(dataServers).find(server => server.authServer === true);
  if (!authServer) throw new Error('Could not find a server with authServer: true. Check your dataServers config.');
  // If the server is a POD, return the root URL instead of https://domain.com/user/data
  return authServer.pod ? (new URL(authServer.baseUrl)).origin : authServer.baseUrl;
};
import jwtDecode from 'jwt-decode';
import urlJoin from 'url-join';
import { defaultToArray, getAclUri, getAclContext, getAuthServerUrl } from './utils';

const authProvider = ({
  dataProvider,
  allowAnonymous = true,
  localAccounts = false,
  checkUser,
  checkPermissions = false
}) => ({
  login: async params => {
    const authServerUrl = await getAuthServerUrl(dataProvider);
    if (localAccounts) {
      const { username, password } = params;
      try {
        const { json } = await dataProvider.fetch(urlJoin(authServerUrl, 'auth/login'), {
          method: 'POST',
          body: JSON.stringify({ username: username.trim(), password: password.trim() }),
          headers: new Headers({ 'Content-Type': 'application/json' })
        });
        const { token } = json;
        localStorage.setItem('token', token);
        // Reload to ensure the dataServer config is reset
        window.location.reload();
      } catch (e) {
        throw new Error('ra.auth.sign_in_error');
      }
    } else {
      let redirectUrl = (new URL(window.location.href)).origin + '/login?login=true';
      if (params.redirect) redirectUrl += '&redirect=' + encodeURIComponent(params.redirect);
      window.location.href = urlJoin(authServerUrl, 'auth?redirectUrl=' + encodeURIComponent(redirectUrl));
    }
  },
  signup: async params => {
    const authServerUrl = await getAuthServerUrl(dataProvider);
    if (localAccounts) {
      const { username, email, password, domain, ...profileData } = params;
      try {
        const { json } = await dataProvider.fetch(urlJoin(authServerUrl, 'auth/signup'), {
          method: 'POST',
          body: JSON.stringify({
            username: username.trim(),
            email: email.trim(),
            password: password.trim(),
            ...profileData
          }),
          headers: new Headers({ 'Content-Type': 'application/json' })
        });
        const { token } = json;
        localStorage.setItem('token', token);
        const { webId } = jwtDecode(token);
        return webId;
      } catch (e) {
        if (e.message === 'email.already.exists') {
          throw new Error('auth.message.user_email_exist');
        } else if (e.message === 'username.already.exists') {
          throw new Error('auth.message.username_exist');
        } else if (e.message === 'username.invalid') {
          throw new Error('auth.message.username_invalid');
        } else {
          throw new Error(e.message || 'ra.auth.sign_in_error');
        }
      }
    } else {
      const redirectUrl = (new URL(window.location.href)).origin + '/login?login=true';
      window.location.href = urlJoin(authServerUrl, 'auth?redirectUrl=' + encodeURIComponent(redirectUrl));
    }
  },
  logout: async () => {
    const authServerUrl = await getAuthServerUrl(dataProvider);
    // Delete token but also any other value in local storage
    localStorage.clear();
    if (localAccounts) {
      // Reload to ensure the dataServer config is reset
      window.location.reload();
      window.location.href = '/';
    } else {
      const baseUrl = (new URL(window.location.href)).origin;
      if (!allowAnonymous) {
        window.location.href = urlJoin(authServerUrl, 'auth/logout?redirectUrl=' + encodeURIComponent(baseUrl + '/login'));
      } else {
        // Redirect to login page after disconnecting from SSO
        // The login page will remove the token, display a notification and redirect to the homepage
        window.location.href = urlJoin(authServerUrl, 'auth/logout?redirectUrl=' + encodeURIComponent(baseUrl + '/login?logout=true'));
      }
    }

    // Avoid displaying immediately the login page
    return '/';
  },
  checkAuth: async () => {
    const token = localStorage.getItem('token');
    if (!token && !allowAnonymous) throw new Error();
  },
  checkUser: userData => {
    if (checkUser) {
      return checkUser(userData);
    } else {
      return true;
    }
  },
  checkError: error => Promise.resolve(),
  getPermissions: async uri => {
    if (!checkPermissions) return true;

    if (!uri || !uri.startsWith('http')) throw new Error('The first parameter passed to getPermissions must be an URL');

    const aclUri = getAclUri(uri);

    try {
      const { json } = await dataProvider.fetch(aclUri);
      return json['@graph'];
    } catch(e) {
      console.warn(`Could not fetch ACL URI ${uri}`);
      return [];
    }
  },
  addPermission: async (uri, agentId, predicate, mode) => {
    if (!uri || !uri.startsWith('http')) throw new Error('The first parameter passed to addPermission must be an URL');

    const aclUri = getAclUri(uri);

    let authorization = {
      '@id': '#' + mode.replace('acl:', ''),
      '@type': 'acl:Authorization',
      [predicate]: agentId,
      'acl:accessTo': uri,
      'acl:mode': mode
    };

    await dataProvider.fetch(aclUri, {
      method: 'PATCH',
      body: JSON.stringify({
        '@context': getAclContext(aclUri),
        '@graph': [authorization]
      })
    });
  },
  removePermission: async (uri, agentId, predicate, mode) => {
    if (!uri || !uri.startsWith('http'))
      throw new Error('The first parameter passed to removePermission must be an URL');

    const aclUri = getAclUri(uri);

    // Fetch current permissions
    let { json } = await dataProvider.fetch(aclUri);

    const updatedPermissions = json['@graph']
      .filter(authorization => !authorization['@id'].includes('#Default'))
      .map(authorization => {
        const modes = defaultToArray(authorization['acl:mode']);
        let agents = defaultToArray(authorization[predicate]);
        if (mode && modes.includes(mode) && agents && agents.includes(agentId)) {
          agents = agents.filter(agent => agent !== agentId);
        }
        return { ...authorization, [predicate]: agents };
      });

    await dataProvider.fetch(aclUri, {
      method: 'PUT',
      body: JSON.stringify({
        '@context': getAclContext(aclUri),
        '@graph': updatedPermissions
      })
    });
  },
  getIdentity: async () => {
    const token = localStorage.getItem('token');
    if (token) {
      const { webId } = jwtDecode(token);

      const { json: webIdData } = await dataProvider.fetch(webId);
      const { json: profileData } = webIdData.url ? await dataProvider.fetch(webIdData.url) : {};

      return {
        id: webId,
        fullName: profileData?.['vcard:given-name'] || profileData?.['pair:label'] || webIdData['foaf:name'],
        profileData,
        webIdData
      };
    }
  },
  resetPassword: async params => {
    const { email } = params;
    const authServerUrl = await getAuthServerUrl(dataProvider);
    try {
      await dataProvider.fetch(urlJoin(authServerUrl, 'auth/reset_password'), {
        method: 'POST',
        body: JSON.stringify({ email: email.trim() }),
        headers: new Headers({ 'Content-Type': 'application/json' })
      });
    } catch (e) {
      throw new Error('app.notification.reset_password_error');
    }
  },
  setNewPassword: async params => {
    const { email, token, password } = params;
    const authServerUrl = await getAuthServerUrl(dataProvider);
    try {
      await dataProvider.fetch(urlJoin(authServerUrl, 'auth/new_password'), {
        method: 'POST',
        body: JSON.stringify({ email: email.trim(), token, password }),
        headers: new Headers({ 'Content-Type': 'application/json' })
      });
    } catch (e) {
      throw new Error('app.notification.new_password_error');
    }
  },
  getAccountSettings: async params => {
    const authServerUrl = await getAuthServerUrl(dataProvider);
    try {
      const { json } = await dataProvider.fetch(urlJoin(authServerUrl, 'auth/account'));
      return json;
    } catch (e) {
      throw new Error('app.notification.get_settings_error');
    }
  },
  updateAccountSettings: async params => {
    const authServerUrl = await getAuthServerUrl(dataProvider);
    try {
      const { email, currentPassword, newPassword } = params;

      await dataProvider.fetch(urlJoin(authServerUrl, 'auth/account'), {
        method: 'POST',
        body: JSON.stringify({ currentPassword, email: email.trim(), newPassword }),
        headers: new Headers({ 'Content-Type': 'application/json' })
      });
    } catch (e) {
      if (e.message === 'auth.account.invalid_password') {
        throw new Error('app.notification.invalid_password');
      }

      throw new Error('app.notification.update_settings_error');
    }
  }
});

export default authProvider;
export const ACL_READ = 'acl:Read';
export const ACL_APPEND = 'acl:Append';
export const ACL_WRITE = 'acl:Write';
export const ACL_CONTROL = 'acl:Control';

export const USER_AGENT = 'acl:agent';
export const GROUP_AGENT = 'acl:agentGroup';
export const CLASS_AGENT = 'acl:agentClass';

export const ANONYMOUS_AGENT = 'foaf:Agent';
export const AUTHENTICATED_AGENT = 'acl:AuthenticatedAgent';

export const rightsToShow = [ACL_READ, ACL_APPEND, ACL_WRITE, ACL_CONTROL];
export const rightsToList = [ACL_READ, ACL_APPEND, ACL_WRITE, ACL_CONTROL];
export const rightsToCreate = [ACL_APPEND, ACL_WRITE, ACL_CONTROL];
export const rightsToEdit = [ACL_APPEND, ACL_WRITE, ACL_CONTROL];
export const rightsToDelete = [ACL_WRITE, ACL_CONTROL];
export const rightsToControl = [ACL_CONTROL];

export const rights = {
  show: rightsToShow,
  list: rightsToList,
  create: rightsToCreate,
  edit: rightsToEdit,
  delete: rightsToDelete,
  control: rightsToControl
};

export const forbiddenErrors = {
  show: 'auth.message.resource_show_forbidden',
  edit: 'auth.message.resource_edit_forbidden',
  delete: 'auth.message.resource_delete_forbidden',
  control: 'auth.message.resource_control_forbidden',
  list: 'auth.message.container_list_forbidden',
  create: 'auth.message.container_create_forbidden'
};

export const resourceRightsLabels = {
  [ACL_READ]: 'auth.right.resource.read',
  [ACL_APPEND]: 'auth.right.resource.append',
  [ACL_WRITE]: 'auth.right.resource.write',
  [ACL_CONTROL]: 'auth.right.resource.control'
};

export const containerRightsLabels = {
  [ACL_READ]: 'auth.right.container.read',
  [ACL_WRITE]: 'auth.right.container.write',
  [ACL_CONTROL]: 'auth.right.container.control'
};
import React, { useEffect } from 'react';
import { usePermissionsOptimized, useRedirect, useNotify, useGetIdentity } from 'react-admin';
import { rights, forbiddenErrors } from '../constants';

const useCheckPermissions = (uri, mode, redirectUrl = '/') => {
  const { identity, loading } = useGetIdentity();
  const { permissions } = usePermissionsOptimized(uri);
  const notify = useNotify();
  const redirect = useRedirect();

  useEffect(() => {
    if (!loading && identity && permissions && !permissions.some(p => rights[mode].includes(p['acl:mode']))) {
      notify(forbiddenErrors[mode], 'error');
      redirect(redirectUrl);
    }
  }, [permissions, identity, redirect, notify, loading]);

  return permissions;
};

export default useCheckPermissions;
